import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'

const Planets = () => {

    const [planet, setPlanet] = useState()

    const { planet_id } = useParams()

    useEffect(() => {

        axios.get(`https://swapi.dev/api/planets/${planet_id}`)
            .then(response => {
                console.log(response.data)
                setItem(response.data)
            })
            .catch(error => console.log(error))
    }, [planet_id])
    return (
        <fieldset>
            <legend>Planets.jsx</legend>
            {
                (planets) ?
                    <>
                        <h1>Name: {planet.name}</h1>
                        <h1>Climate: {climate.cost}</h1>
                        <h1>Terrain: {terrain.cost}</h1>
                        <h1>Surface Water: {surface_water.cost}</h1>
                        <h1>Population: {population.cost}</h1>
                        
                    </> : <h1>Loading....</h1>
            }
        </fieldset>
    )
}

export default Planets
